#include <bits/stdc++.h>

using namespace std;

bool firstRepeating(string &str) {

unordered_map<char,bool> visited;
 for(char const& ch : str){
    if(visited.find(ch) != visited.end()) return false;
    else visited[ch] = true;
  }
  return true;

}

int main() {
string line = "";

//part 1
for(int i = 0; i < line.size(); i++) {
    string set_of_fours = line.substr(i,4);
    if(firstRepeating(set_of_fours)){
        cout << i+4;
        break;
    }
    }

//part 2
for(int i = 0; i < line.size(); i++) {
    string startOfMarker = line.substr(i,14);
    if(firstRepeating(startOfMarker)){
        cout << endl << i+14;
        break;
    }
    }
}
